import { IoIosArrowBack, IoIosArrowForward,AiOutlineWallet,GiBank,VscCreditCard } from "react-icons/all"
import {IconContext} from "react-icons"
import styled from "styled-components"
import { device } from "./Device"


function Dashboard() {

    const Mainboard = styled.div`
        /* max-width: 1500px; */
        padding: 20px;
    `
    const CardTop = styled.div`

        display: grid;
        grid-template-columns: repeat(2) 1fr;
        grid-gap: 20px;
        margin-bottom: 20px;
        @media ${device.tablet} {
            grid-template-columns: repeat(2,1fr);

        }

    `
    const WalletCard = styled.div`

        background:#f0e8ff;
        box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
        border-radius: 5px;

    `
    const IconStyle = styled.div`
        img {
            /* filter: invert(100%) sepia(0%) saturate(0%) hue-rotate(214deg) brightness(102%) contrast(100%); */
            background: #6e3ecb;
            border: 1px solid #fff;
            border-radius: 100%;
            padding: 15px;


        }
    `
    const MyReacIcons = styled.div`
        color: #6e3ecb;
        border: 1px solid #6e3ecb;
        padding: 5px;
        border-radius:100%;
        font-size: 50px;
        align-items:center;
    `

    const ApplyCard = styled.div`
        background: #f7f7f7;
        border-radius: 5px;
        box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
        // display: grid;
        // grid-row: 300px;
        // align-content: center;
        // justify-content: center;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
    `

    const ButtonApply = styled.button`
        background:#4301c2;
        color: #fff;
        padding: 8px;
        min-width: 15rem;
        border: none;


    `
    const MiddleCard = styled.div`
        box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
        padding: 10px;
        background:#fff;
        display: grid;
        grid-template-columns: repeat(auto-fit,minmax(100px, 1fr));
        grid-auto-flow: dense;
        grid-gap: 1rem;
        align-items: center;
        justify-content:space-evenly;
        border-radius: 5px;
        margin-bottom: 20px;
        > figure figcaption {
            font-size: 12px;
            font-weight: 200;
        }

    `
    const LastCard = styled.div`
       display: grid;
        grid-template-columns: repeat(auto-fit,auto);
        grid-auto-flow: dense;
        grid-gap: 1rem;

        background:#f7f7f7;
        box-shadow: inset 4px 0 10px -6px black;
        padding: 2rem;
        border-radius: 5px;
    `
    const OneCard = styled.div`
        display: flex; 
        flex: grow;
        grid-template-columns: repeat(auto-fit,minmax(100px, 1fr));  
        grid-auto-flow: dense;
        justify-content:space-evenly;
        align-items: center;

        background: #fff;
        box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
        border-radius: 10px;
        margin-bottom: 20px;
        @media (max-width: 768px) {
            display: block;
        }

    `
    const SecondCard = styled.div`
        display: grid; 
        grid-template-columns: repeat(auto-fit,minmax(100px, 1fr));  
        grid-auto-flow: dense;
        justify-content:space-evenly;
        align-items: center;

        background: #fff;
        border-radius: 10px;

    `
    const ProcessedBtn = styled.button`
        background:#922c8a;
        color: #fff;
        border-radius: 10px;
    `
    const OnholdBtn = styled.button`
        background: #4856ab;
        color: #fff;
        border-radius: 10px;
    `

    return (
        <Mainboard>
            {/* top */}
            <CardTop>
                <WalletCard>
                    <div style={{display: 'flex',alignContent:'start', justifyContent: 'space-between',fontWeight: '200',fontSize: '20px'}}>
                        <p>YOUR WALLET</p>
                        <p>Feb 2.2021</p>
                    </div>
                    <div style={{display: 'flex',flexDirection: 'column', alignItems: 'center'}}>
                        <p>Wallet Balance</p>
                        <div style={{display: 'flex', alignItems: 'center',}}>
                            <img src="images/naira.png" width="24px" height="24px" alt="naira symbol" />
                            <p style={{marginLeft: '10px',fontSize: '30px'}}>14.61</p>
